job.command:
      cd ${SUBMITTINGDIRECTORY}
setenv STARFPE NO
setenv NODEBUG yes
echo $INPUTFILE0 | grep ':'
if (! $?) then
  set dir      = `echo ${INPUTFILE0} | awk -F\: '{print $1}'`
  set output   = `echo ${INPUTFILE0} | awk -F\: '{print $2}'`
else
  set dir = ${IPUTFILE0};
  set output   = `basename ${dir}`;
endif
if (-r ${output}.root) exit 0;
root.exe -q -b lMuDst.C 'Cosmics.C+("'${dir}'/*/*MuDst.root","'${output}'.root")'  >& ${output}.log
