job.command:

  cd ${SUBMITTINGDIRECTORY}  
  set Name = `basename $PWD`
  setenv NODEBUG yes
  setup 64b
#  starver TFG25f
  starver TFG25k
  set ext = "";
  echo $INPUTFILE0 | grep ':'
  if (! $?) then
    set file      = `echo $INPUTFILE0 | awk -F\: '{print $1}'`
    set f         = `echo $INPUTFILE0 | awk -F\: '{print $2}'`
    set l         = `echo $INPUTFILE0 | awk -F\: '{print $3}'`
    set ext       =  ",${f},${l}"
  else
    set file      = $INPUTFILE0;
    set f         = 1
    set l         = 1000
  endif
  set file2 = `basename ${file} .gener.root`;
  set file1 = `basename ${file2} .event.root`;
  set file3 = `basename ${file1} .root`;
  set name  = `basename ${file3} .daq`${ext};
  echo "${name}"
  set logName  = ${name}B.log
  set MuF   = ${name}.MuDst.root
  if (! -r ${MuF} ) then
     if (-r ${logName}) rm ${logName}
      hostname >&  ${logName}; # dbSnapshot
#     root.exe -q -b -x 'bfc.C(100,"UrQMDFXT,vmc,MC.2021,9p95GeV_fixedTarget_2021,RunG.22168039,StiPulls,McTpcAna,-bbcSim,StiCA,-geantOut,-evout,VMCAlignment,CorrZ,NoHistos,noTags,noRunco,UseCAVxFinder,FXT","'${file}'",0,"'${tagf}'")' >>& ${logName}
#     root.exe -q -b -x 'bfc.C(901,1000,"UrQMDFXT,vmc,MC.2021,9p95GeV_fixedTarget_2021,RunG.22168039,-bbcSim,StiCA,-geantOut,-evout,VMCAlignment,CorrZ,NoHistos,noRunco,FXT","'${file}')' >>& ${logName}
     root.exe -q -b -x 'bfc.C('${f}','${l}',"UrQMDFXT,vmc,MC.2021,9p95GeV_fixedTarget_2021,RunG.22168039,-bbcSim,StiCA,-geantOut,-evout,VMCAlignment,CorrZ,NoHistos,noRunco,FXT,ReverseField,HalfField,-TpcMDF4Error","'${file}'","'${MuF}'")' >>& ${logName}
  endif
