job.command:

cd ${SUBMITTINGDIRECTORY}  
set Name = `basename $PWD`
set file = $INPUTFILE0;
set f    = `echo $file | awk -F\: '{print $1}'`
set l    = `echo $file | awk -F\: '{print $2}'`
set daq  = `echo $file | awk -F\: '{print $3}'`
set gen  = `echo $file | awk -F\: '{print $4}'`
set name  = `basename ${daq} .daq`"_"${f}"_"${l}
echo "${name}"
set logName  = ${name}B.log
if (-r ${logName}) exit 0;
set muDst   = ${name}.MuDst.root
set tagf     = ${name}.tags.root
if ( -r ${muDst} ) exit 0;
root.exe -q -b -x 'bfcMixerTPC23ZBHijingVMC.C('${f}','${l}',"HijingAuAu200","'${daq}'","'${gen}'","'${name}'.root")' >& ${logName}
