job.command:
setenv GROUP_DIR /afs/rhic.bnl.gov/star/group
source $GROUP_DIR/star_cshrc.csh
unset noclobber
setup 64bits
setenv NODEBUG yes

starver SL24y
  cd ${SUBMITTINGDIRECTORY}
  echo $INPUTFILE0 | grep ':'
  if (! $?) then
    set evtsFileF = `echo $INPUTFILE0 | awk -F\: '{print $1}'`
    set f         = `echo $INPUTFILE0 | awk -F\: '{print $2}'`
    set l         = `echo $INPUTFILE0 | awk -F\: '{print $3}'`
  else
    set evtsFileF = $INPUTFILE0;
    set f         = 1
    set l         = 10000
  endif
  if (! -r ${evtsFileF}) exit 1;
#  cp -p ${evtsFileF} .
#  ln -s  ${evtsFileF} .
  set evtsFile = ${evtsFileF};
#  set evtsFile = `basename ${evtsFileF}`;
  echo "${evtsFile}"
#  set filel    = `basename $evtsFile .daq`,${f},${l}
  set filel    = `basename $evtsFile .daq`
  set output  = ${filel}.event.root
  set logFile  =  ${SUBMITTINGDIRECTORY}/${filel}B.log
#  if (-r $logFile) exit 0;
  set muFile = ${filel}.MuDst.root
  if (-r ${SUBMITTINGDIRECTORY}/$muFile) exit 0;
  set bla = ${SUBMITTINGDIRECTORY}/${filel}.bla.root
  if (-r ${bla}) exit 0;
  touch ${bla}
  if (-r ${SUBMITTINGDIRECTORY}/Submitted) then 
    rm ${SUBMITTINGDIRECTORY}/Submitted
  endif
  cd ${SUBMITTINGDIRECTORY}
# root4star -b -q -x  'bfc.C('$f','$l',"DbV20250120 P2019b StiCA FXT EbyET0 CorrZ -mtd picoVtxFXT PicoCovMtxWrite BEmcChkStat -hitfilt evout","'${evtsFile}'","'$output'")' >& ${logFile}
  root4star -b -q -x  'bfc.C('$f','$l',"DbV20250120 P2019b StiCA FXT        CorrZ -mtd picoVtxFXT PicoCovMtxWrite BEmcChkStat -hitfilt evout","'${evtsFile}'","'$output'")' >& ${logFile}
##
##   mv  ${filel}*.root ${logFile} ${SUBMITTINGDIRECTORY}
##   mv  ${filel}*.root  ${SUBMITTINGDIRECTORY}
   rm ${bla}
