  root.exe -b -q  'bfc.C(500,"P2021a,CorrY,-beamLine3D,OSpaceZ2,-HitFilt,StiCA,gmt,'${WestEastOff}''${PicoMode}',BEmcChkStat,NoHistos,noTags,noRunco,-evout,FXT,UseCAVxFinder","'${evtsFile}'")' >& ${logFile}
