job.command:
  cd ${SUBMITTINGDIRECTORY}
#setenv StarEndMakerShell
  setenv STARFPE NO

  set evtsFile = $INPUTFILE0;
  echo "${evtsFile}"
  set filel    = `basename $evtsFile .daq`
  set output  = ${filel}.event.root
  if (! -r ${evtsFile}) exit 1;
  set logFile  = ${filel}.gmt.log
  set gmtFile  = ${filel}.gmt.root
  if ( -r ${SUBMITTINGDIRECTORY}/$gmtFile) exit 0;
  root.exe -b -q  'bfc.C(100000,"in,MakeEvent,gmt,analysis,nodefault","'${evtsFile}'","","'${gmtFile}'")' >& ${logFile}
  gzip ${logFile}
